import React, {useState} from "react";

const Login = () => {

const [userState, setUserState] = useState({
    email: "",
    password: "",
});

const [userData, setUserData] = useState([]);

    const HandleChange = (event) => {
        const {name, value} =event.target;

        setUserState(prevData => {
            return {
                ...prevData, [name]: value
            }
        });

        console.log(userState);
    }

    const HandleSubmit = (event) => {
        event.preventDefault();

        setUserData(prevState => {
            return [
                ...prevState, userState
            ]
        });

        console.log(userData);
    }
    return (
        <div>
            "arrow" Back to home                //small grey   
            Log in below                        //Log in is bold and everything is big
            Don't have an account? Register    //small grey and register is blue
            Email
            Passord
            Log in 

            <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/" className="btn-flat waves-effect" style={{ paddingRight: "410px" }}>
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
            <div className="col s0" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Register</b> below
              </h4>
              <p className="grey-text text-darken-1">
                Already have an account? <Link to="/login">Log in</Link>
              </p>
            </div>
            <form noValidate >
              <div className="input-field col s12">
                <input
               onChange={HandleChange}
               name="name"
               type="text"

                />
                <label htmlFor="name">Name</label>
              </div>
              <div className="input-field col s12">
                <input
                onChange={HandleChange}
                name="email"
                type="email"

                />
                <label htmlFor="">Email</label>
              </div>
              <div className="input-field col s12">
                <input
               onChange={HandleChange}
               name="password"
               type="password"
                />
                <label htmlFor="password">Password</label>
              </div>
              <div className="input-field col s12">
                <input
                   onChange={HandleChange}
                   name="password2"
                   type="password"


                />
                <label htmlFor="password2">Confirm Password</label>
              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  type="submit"   
                  onClick={HandleSubmit}  
                >
                  Sign up
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

        </div>
    )
}

export default Login;
