    const HandleChange = (event) =>{
        const {name, value} = event.target;

        return(
            setState(prevState => {
                return {
                    ...prevState, [name]: value
                }
            })
        )
    }

    const HandleSubmit = (event) => {
            event.preventDefault();
            setNewUserData((prevData) => {
                return[
                    ...prevData, state
                ]
            });
